mapper.select:
with recursive BoardTree as ( select board_no, board_title, board_content, pre_board, board_writer, anon_id, board_date, hit_cnt, 1 as board_level, cast(board_no as char(50)) as board_path, board_enabled from firstproject.board where board_no = #{boardNo}
union all
select b.board_no, b.board_title, b.board_content, b.pre_board, b.board_writer, b.anon_id, b.board_date, b.hit_cnt, bt.board_level + 1, concat(bt.board_path, '-', b.board_no) as board_path, b.board_enabled from firstproject.board b join BoardTree bt on b.pre_board = bt.board_no ) select board_no, board_title, board_content, pre_board, board_writer, anon_id, board_date, hit_cnt, board_level, board_path, board_enabled from BoardTree order by board_path, board_level, board_date desc

mapper.update:
update	board
set		board_title = #{boardTitle},
		board_content = #{boardContent}

			,
			img_name = #{imgName},
			img_type = #{imgType},
			img_data = #{imgData}

where	board_no = #{boardNo}

update	board
set		board_enabled = 0
where	board_no = #{boardNo}

update	reply
set		reply_enabled = 0
where	reply_no = #{replyNo}

update	reply
set		reply_content = #{replyContent}
where	reply_no = #{replyNo}

mapper.select:
select	anon_id, anon_pw,
from	board
where	board_no = #{boardNo}
